import { useEffect, useState } from "react";
import styles from "../styles/App.css";
import elements from "../elements";


function App() {

    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");

    useEffect(() => {
        const length = firstName.length;
        let result = ["", "", ""];
        for (let i = 0; i < length; i++) {
            const oneChar = firstName[i].toUpperCase;
            const twoChar = `${oneChar}${firstName[i + 1]}`;

            if (elements.includes(twoChar)) {
                result = [firstName.slice(0, i), twoChar, firstName.slice(i + 2, length)];
                console.log(result);
                break;
            }

            else if (elements.includes(oneChar)) {
                console.log("One character found");
            }
        }
    }, [firstName]);


    return (
        <>
            <div className="container">
                {/* <h1></h1>
                <h1></h1> */}
                <span>{firstName}</span>
                <span>{lastName}</span>

                <div className="container-2">
                    <div className="row">
                        <div className="column">
                            <label>First Name</label>
                            <input onChange={(e) => setFirstName(e.target.value)}></input>
                        </div>
                        <div className="column">
                            <label>Last Name</label>
                            <input onChange={(e) => setLastName(e.target.value)}></input>
                        </div>
                    </div>

                    <button>Breakify</button>
                </div>

            </div>
        </>
    )
}

export default App;
